import React from "react";
import "./intent.css";

const PaymentUI = () => {
  return (
    <div>
      {/* Intent Container */}
      <div className="intent-container">
        <div className="text">UPI Payment</div>
        <div className="right-area">
          <span className="timer-text">00:10:00</span>
        </div>
      </div>

      {/* Section Heading */}
      <div className="section-to">Make your payment before time runs out!</div>

      {/* Amount Section */}
      <div className="amount-container">
        <p className="amount-text">Amount:</p>
        <p className="price">â‚¹1000</p>
      </div>

      {/* Details Section */}
      <div className="details">
        <span>Bank:</span>
        <span className="bank-value">ICICI Bank</span>
      </div>

      {/* Intent Details */}
      <div className="intent-details-section">
        <div className="account-details">
          <span className="text-value-bank">Account Number:</span>
          <span>1234 5678 9101</span>
          <div className="icon-copy">
            <button>Copy</button>
          </div>
        </div>
      </div>

      {/* Divider */}
      <div className="ant-divider" />

      {/* Additional Info */}
      <div className="section-to">Please complete the payment via UPI to the above details.</div>
    </div>
  );
};

export default PaymentUI;
