import React from 'react';
import { motion } from 'framer-motion';
import './intent.css';

const PaymentUI = () => {
  const buttonVariants = {
    hover: {
      scale: 1.1,
      transition: { duration: 0.3 },
    },
    tap: { scale: 0.9 },
  };

  return (
    <motion.section 
      className="container"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <motion.div className="header" animate={{ y: [50, 0], opacity: [0, 1] }} transition={{ duration: 0.8 }}>
        UPI Intent
      </motion.div>

      <div className="timer-section">
        <p className="timer-label">Payment Time Left</p>
        <p className="timer-value">00:10:00</p>
      </div>

      <div className="amount-section">
        <div className="amount-row">
          <p className="amount-label">
            Amount: <span className="amount-value">â‚¹0</span>
          </p>
        </div>
      </div>

      <div className="button-section">
        {['Google Pay', 'PhonePe', 'Paytm', 'BHIM UPI'].map((text, index) => (
          <motion.button
            className="button"
            key={index}
            variants={buttonVariants}
            whileHover="hover"
            whileTap="tap"
          >
            {text}
          </motion.button>
        ))}
      </div>

      <div className="video-section">
        <motion.div
          className="video-instruction"
          initial={{ x: -50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
        >
          Watch a video for quick deposit instruction
        </motion.div>
        <motion.div
          className="language-options"
          initial={{ x: 50, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ delay: 0.7, duration: 1 }}
        >
          Telugu - Hindi - Tamil - English
        </motion.div>
      </div>
    </motion.section>
  );
};

export default PaymentUI;
