import React, { useState, useEffect } from 'react';
import './intent.css';
import logo1 from "./assets/norton.jpg";
import logo2 from "./assets/pcicertificate.jpg";

const PaymentUI = () => {
  const [timeLeft, setTimeLeft] = useState(600); // 10 minutes in seconds

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft((prevTime) => prevTime - 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [timeLeft]);

  // Format time in mm:ss
  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes.toString().padStart(2, '0')}:${remainingSeconds
      .toString()
      .padStart(2, '0')}`;
  };

  return (
    <section className="container">
      <div className="header">UPI Intent</div>

      {/* Timer Section */}
      <div className="timer-section">
        <p className="timer-label">Payment Time Left</p>
        <p className="timer-value">{formatTime(timeLeft)}</p>
      </div>

      {/* Amount Section */}
      <div className="amount-section">
        <div className="amount-row">
          <p className="amount-label">
            Amount: <span className="amount-value">â‚¹1000</span>
          </p>
        </div>
      </div>

      {/* Payment Options */}
      <div className="button-section">
        <button className="button">
          <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 48 48">
            {/* SVG for Google Pay */}
          </svg>
          Google Pay
        </button>
        <button className="button">
          <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 48 48">
            {/* SVG for PhonePe */}
          </svg>
          PhonePe
        </button>
        <button className="button">
          <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 48 48">
            {/* SVG for Paytm */}
          </svg>
          Paytm
        </button>
        <button className="button">
          <svg xmlns="http://www.w3.org/2000/svg" width="50" height="50" viewBox="0 0 48 48">
            {/* SVG for BHIM UPI */}
          </svg>
          BHIM UPI
        </button>
      </div>

      {/* Additional Logos */}
      <div className="logos-section">
        <button className="logo-button">
          <img src={logo1} alt="Norton Secure Logo" className="logo-image" />
        </button>
        <button className="logo-button">
          <img src={logo2} alt="PCI DSS Certificate Logo" className="logo-image" />
        </button>
      </div>

      {/* Video Section */}
      <div className="video-section">
        <div className="video-instruction" style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
          Watch a video for quick deposit instruction
        </div>
        <div
          className="language-options"
          style={{ fontSize: '16px', color: '#555', marginTop: '5px', textDecoration: 'none' }}
        >
          Telugu - Hindi - Tamil - English
        </div>
      </div>
    </section>
  );
};

export default PaymentUI;
